public class FunctionNodeMapper {

    public static Map<String, Map<String, String>> generateMappings(List<String> functionNodes) {
        Map<String, Map<String, String>> mappings = new HashMap<>();

        for (String node : functionNodes) {
            // split the node string into function name, parameters, and variables
            String[] nodeComponents = node.split("\\s+");

            // create a mapping for the function name
            String functionName = nodeComponents[1];
            Map<String, String> functionMapping = new HashMap<>();
            mappings.put(functionName, functionMapping);

            // create mappings for the function parameters
            String[] parameterNames = nodeComponents[2].split(",");
            for (String parameterName : parameterNames) {
                functionMapping.put(parameterName, "parameter");
            }

            // create mappings for the function variables
            String[] variableNames = Arrays.copyOfRange(nodeComponents, 3, nodeComponents.length);
            for (String variableName : variableNames) {
                functionMapping.put(variableName, "variable");
            }
        }

        return mappings;
    }

}

