import org.hamcrest.BaseMatcher;
import org.hamcrest.Description;
import org.hamcrest.Matcher;

public class GreaterThanMatcher<T extends Comparable<T>> extends BaseMatcher<T> {
    private final T expected;

    public GreaterThanMatcher(T expected) {
        this.expected = expected;
    }

    public static <T extends Comparable<T>> Matcher<T> greaterThan(T expected) {
        return new GreaterThanMatcher<>(expected);
    }

    @Override
    public boolean matches(Object actual) {
        if (actual == null || !(actual instanceof Comparable)) {
            return false;
        }
        @SuppressWarnings("unchecked")
        T actualComparable = (T) actual;
        return actualComparable.compareTo(expected) > 0;
    }

    @Override
    public void describeTo(Description description) {
        description.appendText("a value greater than ").appendValue(expected);
    }
}

