public static void fullGCAndWait() {
    // Create a weak reference to an object
    Object obj = new Object();
    WeakReference<Object> weakRef = new WeakReference<>(obj);

    // Set obj to null to make it eligible for garbage collection
    obj = null;

    // Call System.gc() to initiate a full garbage collection cycle
    System.gc();

    // Wait for the garbage collection cycle to complete
    while (weakRef.get() != null) {
        try {
            Thread.sleep(100);
        } catch (InterruptedException e) {
            // Ignore
        }
    }

    // Call System.runFinalization() to ensure that all finalize methods have been run
    System.runFinalization();
}

