    boolean isValid = true;
    for (String source : sources) {
        switch (validationGoal) {
            case "syntax":
                if (!validateSyntax(source)) {
                    isValid = false;
                }
                break;
            case "semantics":
                if (!validateSemantics(source)) {
                    isValid = false;
                }
                break;
