public static double[][] inverseMatrix(double[][] matrix) throws Exception {
    int n = matrix.length;

    // Check if the matrix is square
    for (int i = 0; i < n; i++) {
        if (matrix[i].length != n) {
            throw new Exception("Matrix is not square");
        }
    }

    // Create the identity matrix
    double[][] identity = new double[n][n];
    for (int i = 0; i < n; i++) {
        identity[i][i] = 1;
    }

    // Apply Gaussian elimination to transform the matrix into
    // row echelon form with the identity matrix on the right
    for (int i = 0; i < n; i++) {
        // Check for zero diagonal element
        if (matrix[i][i] == 0) {
            throw new Exception("Matrix is singular");
        }

        // Divide the current row by the diagonal element
        double factor = matrix[i][i];
        for (int j = 0; j < n; j++) {
            matrix[i][j] /= factor;
            identity[i][j] /= factor;
        }

        // Subtract the current row from the rows below it
        for (int j = i + 1; j < n; j++) {
            factor = matrix[j][i];
            for (int k = 0; k < n; k++) {
                matrix[j][k] -= factor * matrix[i][k];
                identity[j][k] -= factor * identity[i][k];
            }
        }
    }

    // Apply back substitution to transform the matrix into
    // reduced row echelon form with the identity matrix on the left
    for (int i = n - 1; i > 0; i--) {
        for (int j = i - 1; j >= 0; j--) {
            double factor = matrix[j][i];
            for (int k = 0; k < n; k++) {
                matrix[j][k] -= factor * matrix[i][k];
                identity[j][k] -= factor * identity[i][k];
            }
        }
    }

    return identity;
}

