public class ColorUtils {

  // Define a map of named colors and their corresponding RGB values
  private static final Map<String, Color> namedColors = new HashMap<>();
  static {
    namedColors.put("Black", Color.BLACK);
    namedColors.put("White", Color.WHITE);
    namedColors.put("Red", Color.RED);
    namedColors.put("Green", Color.GREEN);
    namedColors.put("Blue", Color.BLUE);
    namedColors.put("Yellow", Color.YELLOW);
    namedColors.put("Cyan", Color.CYAN);
    namedColors.put("Magenta", Color.MAGENTA);
    namedColors.put("Gray", Color.GRAY);
  }

  public static String getClosestColorName(String hexColor) {
    // Parse the input hex color string into an RGB value
    Color inputColor = Color.decode(hexColor);
    int r1 = inputColor.getRed();
    int g1 = inputColor.getGreen();
    int b1 = inputColor.getBlue();

    // Initialize variables to keep track of the closest color and its distance
    String closestColorName = "";
    double closestColorDistance = Double.POSITIVE_INFINITY;

    // Iterate through the named colors and find the closest match
    for (Map.Entry<String, Color> entry : namedColors.entrySet()) {
      Color namedColor = entry.getValue();
      int r2 = namedColor.getRed();
      int g2 = namedColor.getGreen();
      int b2 = namedColor.getBlue();

      // Calculate the Euclidean distance between the two colors
      double distance = Math.sqrt(Math.pow(r2 - r1, 2) + Math.pow(g2 - g1, 2) + Math.pow(b2 - b1, 2));

      // Update the closest color and its distance if necessary
      if (distance < closestColorDistance) {
        closestColorName = entry.getKey();
        closestColorDistance = distance;
      }
    }

    // Return the name of the closest color
    return closestColorName;
  }
}

