import org.apache.lucene.index.TermState;
import org.apache.lucene.search.IndexSearcher;
import org.apache.lucene.search.ScoreDoc;
import org.apache.lucene.search.TermQuery;
import org.apache.lucene.search.TopDocs;
import org.apache.lucene.store.FSDirectory;

public Set<String> getIndexedURLsForTerm(String term) throws IOException {
    Set<String> urls = new HashSet<String>();

    // Open the Lucene index directory
    FSDirectory indexDir = FSDirectory.open(Paths.get("path/to/index/directory"));

    // Open an IndexReader to read the index
    IndexReader indexReader = DirectoryReader.open(indexDir);

    // Create an IndexSearcher to search the index
    IndexSearcher indexSearcher = new IndexSearcher(indexReader);

    // Create a TermQuery for the given term
    Term termObj = new Term("contents", term);
    TermQuery query = new TermQuery(termObj);

    // Get the TermContext for the given term
    TermContext termContext = TermContext.build(indexReader.getContext(), termObj);

    // Get the TermState for the given term
    TermState termState = termContext.get(indexReader.leaves().get(0));

    // Execute the query to get the matching documents
    TopDocs topDocs = indexSearcher.search(query, Integer.MAX_VALUE);

    // Iterate over the ScoreDocs to get the URLs of the matching documents
    for (ScoreDoc scoreDoc : topDocs.scoreDocs) {
        int docId = scoreDoc.doc;
        String url = indexSearcher.doc(docId).get("url");
        urls.add(url);
    }

    // Close the IndexReader and the index directory
    indexReader.close();
    indexDir.close();

    return urls;
}

