    int index = 0;

    if (format.charAt(0) == '%') {
        index++;
        if (format.charAt(index) == '*') {
            if (advance) {
                length = -1;
            }
            index++;
        } else {
            while (index < format.length() && Character.isDigit(format.charAt(index))) {
                length = length * 10 + Character.getNumericValue(format.charAt(index));
                index++;
            }
        }
        if (advance) {
            index++;
        }
    }

