public class JPEGWriter {

    /**
     * Convenience method for initializing a writer for the JPEG image format.
     * @param output the output stream to write the image to.
     * @param quality the quality of the JPEG image, ranging from 0 to 1.
     * @return an ImageWriter for the JPEG format.
     * @throws IOException if an I/O error occurs while creating the writer.
     */
    public static ImageWriter createJPEGWriter(OutputStream output, float quality) throws IOException {
        // Obtain an ImageWriter for the JPEG format.
        ImageWriter writer = ImageIO.getImageWritersByFormatName("jpeg").next();

        // Set the compression quality for the JPEG image.
        JPEGImageWriteParam param = (JPEGImageWriteParam) writer.getDefaultWriteParam();
        param.setCompressionMode(JPEGImageWriteParam.MODE_EXPLICIT);
        param.setCompressionQuality(quality);

        // Create an ImageOutputStream to write the image data.
        ImageOutputStream ios = ImageIO.createImageOutputStream(output);
        writer.setOutput(ios);

        return writer;
    }
}

