public class CheckpointHasher {

    public static byte[] getCheckpointHash(byte[][] checkpointData) throws NoSuchAlgorithmException {
        MessageDigest digest = MessageDigest.getInstance("SHA-256");
        byte[] concatenatedData = concatenateData(checkpointData);
        byte[] hash = digest.digest(concatenatedData);
        return hash;
    }

    private static byte[] concatenateData(byte[][] data) {
        int totalLength = 0;
        for (byte[] d : data) {
            totalLength += d.length;
        }
        byte[] result = new byte[totalLength];
        int currentIndex = 0;
        for (byte[] d : data) {
            System.arraycopy(d, 0, result, currentIndex, d.length);
            currentIndex += d.length;
        }
        return result;
    }
}

