    double lowestValue = Double.POSITIVE_INFINITY;
    boolean hasFiniteValue = false;

    for (double value : data) {
        if (Double.isNaN(value)) {
            return Double.NaN;
        } else if (Double.isFinite(value)) {
            hasFiniteValue = true;
            if (value < lowestValue) {
                lowestValue = value;
            }
        } else if (value == Double.NEGATIVE_INFINITY) {
            return Double.NEGATIVE_INFINITY;
        }
    }

    if (lowestValue == Double.POSITIVE_INFINITY) {
        if (hasFiniteValue) {
            return Double.NaN; // dataset contains only NaN values
        } else {
            return Double.POSITIVE_INFINITY; // dataset contains only infinite values
        }
    } else {
        return lowestValue;
    }
}

