public static double[] householderReflection(double[] u, double gamma, double[] x) {
    int n = u.length;
    double[] y = new double[n];
    double uTx = 0.0;

    // Compute uTx = u^T x
    for (int i = 0; i < n; i++) {
        uTx += u[i] * x[i];
    }

    // Compute y = x + gamma * (uTx) * u
    for (int i = 0; i < n; i++) {
