import org.apache.commons.compress.compressors.xz.XZCompressorOutputStream;

import java.io.*;

public static void tarXzContents(String revision, String basepath, OutputStream outputStream) throws IOException {
    ProcessBuilder processBuilder = new ProcessBuilder("git", "archive", "--format", "tar", revision, basepath);
    Process process = processBuilder.start();
    InputStream inputStream = process.getInputStream();

    XZCompressorOutputStream compressorOutputStream = new XZCompressorOutputStream(outputStream);
    TarArchiveOutputStream archiveOutputStream = new TarArchiveOutputStream(compressorOutputStream);

    BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));
    String line;
    while ((line = reader.readLine()) != null) {
        TarArchiveEntry entry = new TarArchiveEntry(line);
        archiveOutputStream.putArchiveEntry(entry);

        FileInputStream fileInputStream = new FileInputStream(line);
        byte[] buffer = new byte[1024];
        int len;
        while ((len = fileInputStream.read(buffer)) > 0) {
            archiveOutputStream.write(buffer, 0, len);
        }
        fileInputStream.close();

        archiveOutputStream.closeArchiveEntry();
    }

    archiveOutputStream.finish();
    compressorOutputStream.finish();
    outputStream.flush();
}

