import java.net.URL;
import java.util.ArrayList;
import java.util.List;
import javax.net.ssl.HttpsURLConnection;

public class OAuthChecker {

    public static void checkOAuthService(String oauthUrl) {
        try {
            URL url = new URL(oauthUrl);
            HttpsURLConnection connection = (HttpsURLConnection) url.openConnection();
            connection.setRequestMethod("GET");
            connection.connect();
            int responseCode = connection.getResponseCode();

            if (responseCode == HttpsURLConnection.HTTP_OK) {
                String response = "";
                int bytesRead = -1;
                byte[] buffer = new byte[4096];
                while ((bytesRead = connection.getInputStream().read(buffer)) != -1) {
                    response += new String(buffer, 0, bytesRead);
                }

                List<String> openEndpoints = new ArrayList<String>();
                if (!response.contains("https://")) {
                    openEndpoints.add(oauthUrl);
                }

                if (openEndpoints.size() > 0) {
                    System.out.println("Warning: The following endpoints are not using HTTPS:");
                    for (String endpoint : openEndpoints) {
                        System.out.println("- " + endpoint);
                    }
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

