public static int getMinimumEncodedSize(byte[] varInt) {
    int value = 0;
    int length = varInt.length;
    for (int i = 0; i < length; i++) {
        byte b = varInt[i];
        value |= (b & 0x7F) << (i * 7);
        if ((b & 0x80) == 0) {
            break;
        }
    }
    if (value < (1 << 7)) {
        return 1;
    } else if (value < (1 << 14)) {
        return 2;
    } else if (value < (1 << 21)) {
        return 3;
    } else if (value < (1 << 28)) {
        return 4;
    } else {
        return 5;
    }
}

