public String determineContentType(String message) {
    // Define an array of content type rules in the order they should be checked
    String[] contentTypeRules = {"text/plain", "text/html", "application/json", "application/xml"};

    // Loop through each content type rule
    for (String contentTypeRule : contentTypeRules) {
        // Check if the message matches the current content type rule
        if (matchesContentTypeRule(message, contentTypeRule)) {
            return contentTypeRule; // Return the matched content type
        }
    }

    return "unknown"; // No content type rules matched, return "unknown"
}

private boolean matchesContentTypeRule(String message, String contentTypeRule) {
    // Define a regular expression pattern for the content type rule
    String pattern = "^\\s*" + contentTypeRule + "\\b";

    // Compile the pattern into a regular expression
    Pattern regex = Pattern.compile(pattern, Pattern.CASE_INSENSITIVE);

    // Match the regular expression against the message
    Matcher matcher = regex.matcher(message);

    // Check if the regular expression matched the beginning of the message
    return matcher.find();
}

