public static double[][] cofactor(double[][] matrix) {
    int rows = matrix.length;
    int cols = matrix[0].length;

    double[][] result = new double[rows][cols];

    for (int i = 0; i < rows; i++) {
        for (int j = 0; j < cols; j++) {
            result[i][j] = Math.pow(-1, i+j) * determinant(minor(matrix, i, j));
        }
    }

    return result;
}

private static double[][] minor(double[][] matrix, int row, int col) {
    int rows = matrix.length;
    int cols = matrix[0].length;

    double[][] result = new double[rows-1][cols-1];

    int m = 0;
    int n = 0;
    for (int i = 0; i < rows; i++) {
        if (i == row) continue;
        for (int j = 0; j < cols; j++) {
            if (j == col) continue;
            result[m][n] = matrix[i][j];
            n++;
        }
        m++;
        n = 0;
    }

    return result;
}

private static double determinant(double[][] matrix) {
    int rows = matrix.length;
    int cols = matrix[0].length;

    if (rows != cols) {
        throw new IllegalArgumentException("Matrix must be square");
    }

    if (rows == 1) {
        return matrix[0][0];
    }

    if (rows == 2) {
        return matrix[0][0]*matrix[1][1] - matrix[0][1]*matrix[1][0];
    }

    double det = 0;

    for (int j = 0; j < cols; j++) {
        det += Math.pow(-1, j) * matrix[0][j] * determinant(minor(matrix, 0, j));
    }

    return det;
}

