public static double populationCovariance(double[] x, double[] y) {
    if (x.length != y.length) {
        throw new IllegalArgumentException("Arrays must be of equal length");
    }

    int n = x.length;
    double sumX = 0.0;
    double sumY = 0.0;
    double sumXY = 0.0;
    double sumXX = 0.0;
    double sumYY = 0.0;

    for (int i = 0; i < n; i++) {
        if (!Double.isFinite(x[i]) || !Double.isFinite(y[i])) {
            return Double.NaN;
        }

        sumX += x[i];
        sumY += y[i];
        sumXY += x[i] * y[i];
        sumXX += x[i] * x[i];
        sumYY += y[i] * y[i];
    }

    double covariance = (sumXY - (sumX * sumY) / n) / (n - 1);

    if (Double.isNaN(covariance)) {
        return 0.0;
    }

    return covariance;
}

