public static double[] computeGammas(double[] x) {
    int n = x.length;
    double[] gammas = new double[n];

    for (int i = 0; i < n; i++) {
        double sigma = 0.0;
        for (int j = i; j < n; j++) {
            sigma += x[j] * x[j];
        }

        if (sigma == 0.0) {
            gammas[i] = 0.0;
        } else {
            double mu = Math.sqrt(x[i] * x[i] + sigma);
            if (x[i] <= 0.0) {
                x[i] -= mu;
            } else {
                x[i] = -sigma / (x[i] + mu);
            }
            gammas[i] = x[i] / mu;
            x[i] = 1.0;
            for (int j = i + 1; j < n; j++) {
                x[j] /= x[i];
            }
        }
    }

    return gammas;
}

