public static boolean itemsetAppearsInPeriod(int[] itemset, int[] period) {
    if (itemset.length > period.length) {
        return false; // Itemset is longer than period, so it cannot appear
    }
    for (int i = 0; i <= period.length - itemset.length; i++) {
        boolean itemsetMatches = true;
        for (int j = 0; j < itemset.length; j++) {
            if (itemset[j] != period[i+j]) {
                itemsetMatches = false;
                break; // Itemset doesn't match at this position, move to the next
            }
        }
        if (itemsetMatches) {
            return true; // Itemset matches at this position, so it appears in the period
        }
    }
    return false; // Itemset doesn't appear in the period
}

