import java.util.Arrays;
import org.bitcoinj.core.*;

public class PaymentScriptPubKey {

  public static byte[] createScriptPubKey(byte[] rawPublicKey) {
    // Create a ScriptBuilder object to construct the scriptPubKey
    ScriptBuilder builder = new ScriptBuilder();

    // Add the raw public key to the script
    builder.data(rawPublicKey);

    // Add the OP_CHECKSIG opcode to the script
    builder.op(ScriptOpCodes.OP_CHECKSIG);

    // Build the scriptPubKey
    Script scriptPubKey = builder.build();

    // Convert the scriptPubKey to a byte array and return it
    return scriptPubKey.getProgram();
  }

  public static void main(String[] args) {
    // Define a raw public key in hexadecimal format
    String publicKeyHex = "04c5b47e8b547f5cf361fba3b64cf65c42f455aa940cfc263524d67d550f387fe" +
        "c9d0441d2d2a73c6dcbe63ea782888a6d3047326c9f70cf3314df1b4f4b4d50";

    // Convert the public key to a byte array
    byte[] publicKeyBytes = Utils.HEX.decode(publicKeyHex);

    // Create the scriptPubKey
    byte[] scriptPubKey = createScriptPubKey(publicKeyBytes);

    // Print the scriptPubKey in hexadecimal format
    System.out.println(Utils.HEX.encode(scriptPubKey));
  }
}

