public static void shiftMatrix(int[][] matrix) {
    // get the magnitude of each element in the matrix
    int magnitude = getMagnitude(matrix);

    // generate a random direction vector
    int dx = (int) (Math.random() * 3) - 1; // random number between -1 and 1
    int dy = (int) (Math.random() * 3) - 1; // random number between -1 and 1

    // shift the matrix in the random direction
    for (int i = 0; i < matrix.length; i++) {
        for (int j = 0; j < matrix[i].length; j++) {
            int new_i = i + magnitude * dx;
            int new_j = j + magnitude * dy;

            // check if the new position is within the matrix bounds
            if (new_i >= 0 && new_i < matrix.length && new_j >= 0 && new_j < matrix[i].length) {
                // swap the elements at the current and new positions
                int temp = matrix[i][j];
                matrix[i][j] = matrix[new_i][new_j];
                matrix[new_i][new_j] = temp;
            }
        }
    }
}

private static int getMagnitude(int[][] matrix) {
    // get the sum of the absolute values of all elements in the matrix
    int magnitude = 0;
    for (int[] row : matrix) {
        for (int elem : row) {
            magnitude += Math.abs(elem);
        }
    }
    return magnitude;
}

