import java.io.*;
import java.net.*;

public class Client {
    private Socket socket;
    private PrintWriter out;

    public void connect(String serverAddress, int port) throws IOException {
        // Create a new socket and connect to the server
        socket = new Socket(serverAddress, port);

        // Create a PrintWriter object for sending messages to the server
        out = new PrintWriter(socket.getOutputStream(), true);

        // Start the sender thread
        Thread senderThread = new Thread(new Sender());
        senderThread.start();
    }

    private class Sender implements Runnable {
        @Override
        public void run() {
            try {
                BufferedReader userInput = new BufferedReader(new InputStreamReader(System.in));
                String message;

                // Read messages from the user and send them to the server
                while ((message = userInput.readLine()) != null) {
                    out.println(message);
                }
            } catch (IOException e) {
                System.err.println("IOException: " + e.getMessage());
            } finally {
                // Close the socket and exit the program when the sender thread is finished
                try {
                    socket.close();
                } catch (IOException e) {
                    System.err.println("IOException: " + e.getMessage());
                }
                System.exit(0);
            }
        }
    }
}

