public static char processRtfCommandParameter(String rtfCommandParameter) {
    if (rtfCommandParameter == null || rtfCommandParameter.length() == 0) {
        throw new IllegalArgumentException("RTF command parameter cannot be null or empty");
    }

    int unicodeValue;
    if (rtfCommandParameter.startsWith("\\u")) {
        // Process hexadecimal Unicode value
        String hexString = rtfCommandParameter.substring(2);
        try {
            unicodeValue = Integer.parseInt(hexString, 16);
        } catch (NumberFormatException e) {
            throw new IllegalArgumentException("Invalid hexadecimal Unicode value: " + hexString);
        }
    } else {
        // Process decimal Unicode value
        try {
            unicodeValue = Integer.parseInt(rtfCommandParameter);
        } catch (NumberFormatException e) {
            throw new IllegalArgumentException("Invalid decimal Unicode value: " + rtfCommandParameter);
        }
    }

    if (unicodeValue < 0 || unicodeValue > Character.MAX_VALUE) {
        throw new IllegalArgumentException("Unicode value out of range: " + unicodeValue);
    }

    return (char) unicodeValue;
}

