import java.nio.ByteBuffer;
import java.nio.ByteOrder;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.util.Base64;

public class P2SHScriptPubKey {

    /**
     * Creates a scriptPubKey for a P2SH transaction.
     *
     * @param scriptHash the hash of the redeem script
     * @return a scriptPubKey in Base64-encoded string format
     */
    public static String createScriptPubKey(String scriptHash) {
        byte[] scriptHashBytes = Base64.getDecoder().decode(scriptHash);
        byte[] scriptPubKeyBytes = new byte[23];
        scriptPubKeyBytes[0] = (byte) 0xA9;
        scriptPubKeyBytes[1] = (byte) 0x14;
        System.arraycopy(scriptHashBytes, 0, scriptPubKeyBytes, 2, scriptHashBytes.length);
        scriptPubKeyBytes[22] = (byte) 0x87;
        return Base64.getEncoder().encodeToString(scriptPubKeyBytes);
    }

    /**
     * Computes the SHA-256 hash of a byte array.
     *
     * @param bytes the byte array to hash
     * @return the hash as a byte array
     * @throws NoSuchAlgorithmException if SHA-256 is not available
     */
    private static byte[] sha256(byte[] bytes) throws NoSuchAlgorithmException {
        MessageDigest md = MessageDigest.getInstance("SHA-256");
        return md.digest(bytes);
    }

    /**
     * Computes the RIPEMD-160 hash of a byte array.
     *
     * @param bytes the byte array to hash
     * @return the hash as a byte array
     * @throws NoSuchAlgorithmException if RIPEMD-160 is not available
     */
    private static byte[] ripemd160(byte[] bytes) throws NoSuchAlgorithmException {
        MessageDigest md = MessageDigest.getInstance("RIPEMD160");
        return md.digest(bytes);
    }

    public static void main(String[] args) throws NoSuchAlgorithmException {
        // Example usage:
        String redeemScript = "OP_DUP OP_HASH160 2a269c874a53f65b4987f1d73dc65c10b9cb88f9 OP_EQUALVERIFY OP_CHECKSIG";
        byte[] redeemScriptBytes = redeemScript.getBytes();
        byte[] redeemScriptHashBytes = ripemd160(sha256(redeemScriptBytes));
        String redeemScriptHash = Base64.getEncoder().encodeToString(redeemScriptHashBytes);
        String scriptPubKey = createScriptPubKey(redeemScriptHash);
        System.out.println(scriptPubKey);
    }
}

