public byte[] retrieveByteArrayField(byte[] dest, int off, int len) throws IOException {
    // Ensure that the destination array is not null
    if (dest == null) {
        throw new IllegalArgumentException("Destination byte array cannot be null.");
    }

    // Ensure that the offset and length values are valid
    if (off < 0 || len < 0 || off + len > dest.length) {
        throw new IllegalArgumentException("Invalid offset and/or length values.");
    }

    // Read the byte array from the input stream
    byte[] buffer = new byte[len];
    int bytesRead = inputStream.read(buffer);

    // If the entire data item could not be read, return a truncated value
    if (bytesRead < len) {
        byte[] truncatedBuffer = new byte[bytesRead];
        System.arraycopy(buffer, 0, truncatedBuffer, 0, bytesRead);
        buffer = truncatedBuffer;
    }

    // Copy the retrieved byte array into the destination array
    System.arraycopy(buffer, 0, dest, off, buffer.length);

