import java.nio.charset.StandardCharsets;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.util.ArrayList;
import java.util.List;

public class NumberVerifier {

    private List<String> submittedNumbers;

    public NumberVerifier() {
        submittedNumbers = new ArrayList<>();
    }

    public boolean addAndVerifyNumber(String number, String hash) {
        // Verify that the hash matches the number
        String calculatedHash = calculateHash(number);
        if (!calculatedHash.equals(hash)) {
            return false;
        }

        // Add the number to the list of submitted numbers
        submittedNumbers.add(number);
        return true;
    }

    private String calculateHash(String input) {
        try {
            MessageDigest digest = MessageDigest.getInstance("SHA-256");
            byte[] hash = digest.digest(input.getBytes(StandardCharsets.UTF_8));
            StringBuilder hexString = new StringBuilder();
            for (byte b : hash) {
                String hex = Integer.toHexString(0xff & b);
                if (hex.length() == 1) {
                    hexString.append('0');
                }
                hexString.append(hex);
            }
            return hexString.toString();
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException(e);
        }
    }

    public List<String> getSubmittedNumbers() {
        return submittedNumbers;
    }
}

