import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.security.KeyStore;
import java.security.cert.Certificate;

public class KeyStoreUtils {

    public static void deleteCertificate(String keystoreFile, String keystorePassword, String alias) throws Exception {

        // Load the KeyStore
        KeyStore keystore = KeyStore.getInstance(KeyStore.getDefaultType());
        FileInputStream fis = new FileInputStream(keystoreFile);
        keystore.load(fis, keystorePassword.toCharArray());
        fis.close();

        // Delete the certificate with the given alias
        if (keystore.containsAlias(alias)) {
            keystore.deleteEntry(alias);
        } else {
            throw new Exception("Alias not found in keystore.");
        }

        // Save the updated KeyStore
        FileOutputStream fos = new FileOutputStream(keystoreFile);
        keystore.store(fos, keystorePassword.toCharArray());
        fos.close();

    }

}
```

