public class MapPosition {
    private double latitude;
    private double longitude;
    private double distance; // distance in meters from reference location
    private double direction; // direction in degrees from reference location

    public MapPosition(double latitude, double longitude, double distance, double direction) {
        this.latitude = latitude;
        this.longitude = longitude;
        this.distance = distance;
        this.direction = direction;
    }

    public MapPosition(double latitude, double longitude) {
        this(latitude, longitude, 0, 0);
    }

    public void setPositionFromReference(double refLatitude, double refLongitude) {
        double R = 6371e3; // Earth's radius in meters
        double lat1 = Math.toRadians(refLatitude);
        double lat2 = Math.toRadians(this.latitude);
        double deltaLat = Math.toRadians(this.latitude - refLatitude);
        double deltaLon = Math.toRadians(this.longitude - refLongitude);

        double a = Math.sin(deltaLat / 2) * Math.sin(deltaLat / 2) +
                Math.cos(lat1) * Math.cos(lat2) *
                        Math.sin(deltaLon / 2) * Math.sin(deltaLon / 2);
        double c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

        this.distance = R * c;
        double y = Math.sin(deltaLon) * Math.cos(lat2);
        double x = Math.cos(lat1) * Math.sin(lat2) -
                Math.sin(lat1) * Math.cos(lat2) * Math.cos(deltaLon);
        this.direction = Math.toDegrees(Math.atan2(y, x));
    }

    public double getLatitude() {
        return this.latitude;
    }

    public double getLongitude() {
        return this.longitude;
    }

    public double getDistance() {
        return this.distance;
    }

    public double getDirection() {
        return this.direction;
    }
}

