public long writeSetStringRecordToLog(String record) {
    boolean isTempFile = checkIfTempFile(record);
    if (isTempFile) {
        return -1L; // return dummy negative LSN if record is a temporary file
    }
    // write record to log file
    try (FileWriter fw = new FileWriter("log.txt", true);
         BufferedWriter bw = new BufferedWriter(fw);
         PrintWriter out = new PrintWriter(bw)) {
        long lsn = System.currentTimeMillis(); // generate LSN using current time in milliseconds
        out.println(lsn + " " + record); // write LSN and record to log file
        return lsn; // return generated LSN
    } catch (IOException e) {
        e.printStackTrace();
        return -1L; // return dummy negative LSN if an exception occurs
    }
}

private boolean checkIfTempFile(String record) {
    // check if record is a temporary file by checking its file extension
    String[] parts = record.split("\\.");
    String extension = parts[parts.length - 1];
    return extension.equals("tmp");
}

