```
import java.util.HashMap;
import java.util.Map;

import aimacode.bn.BayesNet;
import aimacode.bn.Node;
import aimacode.bn.inference.InferenceAlgorithm;
import aimacode.bn.inference.JoinTree;
import aimacode.bn.inference.VariableElimination;
import aimacode.bn.utils.SetOps;

public class LikelihoodWeighting {

    public static Map<Node, Object> likelihoodWeighting(BayesNet bn, Node queryNode, Map<Node, Object> evidence, int samples) {
        Map<Node, Object> result = new HashMap<Node, Object>();
        int count = 0;
        for (int i = 0; i < samples; i++) {
            Map<Node, Object> sample = weightedSample(bn, evidence);
            Object value = sample.get(queryNode);
            if (value != null) {
                SetOps.addToMap(result, value, 1);
                count++;
            }
        }
        for (Object value : result.keySet()) {
            result.put((Node) value, result.get(value) / count);
        }
        return result;
    }

    private static Map<Node, Object> weightedSample(BayesNet bn, Map<Node, Object> evidence) {
        double weight = 1.0;
        Map<Node, Object> sample = new HashMap<Node, Object>();
        for (Node node : bn.getVariableListTopologicallySorted()) {
            if (evidence.containsKey(node)) {
                sample.put(node, evidence.get(node));
                weight *= bn.getProb(node, sample);
            } else {
                Object value = sampleValue(node, sample);
                sample.put(node, value);
            }
        }
        sample.put(null, weight);
        return sample;
    }

    private static Object sampleValue(Node node, Map<Node, Object> sample) {
        double[] distribution = new double[node.getDomain().size()];
        for (int i = 0; i < distribution.length; i++) {
            Object value = node.getDomain().get(i);
            sample.put(node, value);
            distribution[i] = node.getProb(sample);
        }
