public byte[] retrieveByteArray(InputStream inputStream) throws IOException {
    // Determine the size of the byte array to retrieve
    int size = inputStream.readInt();

    // Create a new byte array of the same size
    byte[] byteArray = new byte[size];

    // Read the bytes from the input stream into the byte array
    int bytesRead = 0;
    while (bytesRead < size) {
        int count = inputStream.read(byteArray, bytesRead, size - bytesRead);
        if (count == -1) {
            throw new EOFException("Unexpected end of input stream");
        }
        bytesRead += count;
    }

    return byteArray;
}

