private int getNextSequenceNumber() {
    int sequenceNumber = 0;
    try {
        BufferedReader reader = new BufferedReader(new FileReader("checkpoint.txt"));
        String line;
        while ((line = reader.readLine()) != null) {
            int lineSequenceNumber = Integer.parseInt(line.split(" ")[0]);
            if (lineSequenceNumber > sequenceNumber) {
                sequenceNumber = lineSequenceNumber;
            }
        }
        reader.close();
    } catch (IOException e) {
        e.printStackTrace();
    }
    return sequenceNumber + 1;
}

