public ClassLoader getClassLoaderForInterfaces(ClassLoader givenLoader, Class<?>... interfaces) {
    boolean canSeeAllInterfaces = true;
    for (Class<?> interfaceClass : interfaces) {
        if (givenLoader.loadClass(interfaceClass.getName()) != interfaceClass) {
            canSeeAllInterfaces = false;
            break;
