public static long parseFileSize(String size) throws NumberFormatException {
    String[] tokens = size.split("(?i)(?<=[0-9])(?=[a-z])");
    if (tokens.length != 2) {
        throw new NumberFormatException("Invalid file size format: " + size);
    }
    long fileSize = Long.parseLong(tokens[0]);
    String unit = tokens[1].toLowerCase();
    switch (unit) {
        case "b":
            break;
        case "kb":
            fileSize *= 1024L;
            break;
        case "mb":
            fileSize *= 1024L * 1024L;
            break;
        case "gb":
            fileSize *= 1024L * 1024L * 1024L;
            break;
        case "tb":
            fileSize *= 1024L * 1024L * 1024L * 1024L;
            break;
        default:
            throw new NumberFormatException("Invalid file size unit: " + unit);
    }
    return fileSize;
}

