public static String getNextIdentifier(String currentIdentifier) {
    String nextIdentifier = "";
    int length = currentIdentifier.length();
    char lastChar = currentIdentifier.charAt(length - 1);

    if (Character.isDigit(lastChar)) {
        // If the last character is a digit, increment it
        int lastDigit = Character.getNumericValue(lastChar);
        nextIdentifier = currentIdentifier.substring(0, length - 1) + (++lastDigit);
    } else {
        // If the last character is a letter, add "1" to the end
        nextIdentifier = currentIdentifier + "1";
    }

    return nextIdentifier;
}

