import javax.xml.crypto.dsig.XMLSignature;
import javax.xml.crypto.dsig.XMLSignatureFactory;
import javax.xml.crypto.dsig.dom.DOMValidateContext;
import javax.xml.crypto.dsig.keyinfo.KeyInfo;
import javax.xml.crypto.dsig.keyinfo.X509Data;
import javax.xml.crypto.dsig.keyinfo.X509IssuerSerial;
import javax.xml.crypto.dsig.keyinfo.X509Certificate;

public String getSignatureProperties(Element signatureElement) throws Exception {
    // Create a DOMValidateContext using the signature element
    DOMValidateContext valContext = new DOMValidateContext(null, signatureElement);

    // Get the XMLSignatureFactory
    XMLSignatureFactory sigFactory = XMLSignatureFactory.getInstance("DOM");

    // Create an XMLSignature object from the signature element
    XMLSignature signature = sigFactory.unmarshalXMLSignature(valContext);

    // Get the KeyInfo from the signature
    KeyInfo keyInfo = signature.getKeyInfo();

    // Get the X509Data from the KeyInfo
    X509Data x509Data = (X509Data) keyInfo.getContent().get(0);

    // Get the X509IssuerSerial from the X509Data
    X509IssuerSerial issuerSerial = x509Data.getIssuerSerial();

    // Get the X509Certificate from the X509Data
    X509Certificate cert = x509Data.getX509Certificate();

    // Get the signature properties
    String properties = "Issuer: " + issuerSerial.getIssuerName().toString() +
                        "\nSerial Number: " + issuerSerial.getSerialNumber().toString() +
                        "\nCertificate: " + cert.toString();

    return properties;
}

