import java.lang.reflect.Method;
import java.lang.reflect.Modifier;
import java.util.Arrays;

public class MethodFinder {

  public static Method findMethod(Class<?> clazz, String methodName, Class<?>... parameterTypes) {
    try {
      Method method = clazz.getDeclaredMethod(methodName, parameterTypes);
      // Make the method accessible if it is not public
      if (!Modifier.isPublic(method.getModifiers())) {
        method.setAccessible(true);
      }
      return method;
    } catch (NoSuchMethodException ex) {
      // Check if the method is defined in a superclass or interface
      Class<?> superclass = clazz.getSuperclass();
      if (superclass != null) {
        return findMethod(superclass, methodName, parameterTypes);
      } else {
        // Check if the method is defined in any of the implemented interfaces
        for (Class<?> iface : clazz.getInterfaces()) {
          try {
            return findMethod(iface, methodName, parameterTypes);
          } catch (NoSuchMethodException e) {
            // Ignore and try next interface
          }
        }
        // Method not found in any superclass or interface
        throw new NoSuchMethodException("Method not found: " + methodName + "(" + Arrays.toString(parameterTypes) + ")");
      }
    }
  }
}

