public class GitHelper {

    private final Git git;

    public GitHelper(String repositoryPath) throws IOException {
        this.git = Git.open(new File(repositoryPath));
    }

    public List<RevCommit> getCommits() throws GitAPIException {
        List<RevCommit> commits = new ArrayList<>();
        Iterable<RevCommit> allCommits = git.log().call();
        for (RevCommit commit : allCommits) {
            commits.add(commit);
        }
        return commits;
    }

    public RevCommit getCommitById(String commitId) throws GitAPIException {
        ObjectId objId = ObjectId.fromString(commitId);
        return git.getRepository().parseCommit(objId);
    }
}

