import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;

public class ChecksumSetter {

    public static void setChecksumType(String fileName, String algorithm) throws NoSuchAlgorithmException, IOException {
        MessageDigest md = MessageDigest.getInstance(algorithm);
        FileInputStream fis = new FileInputStream(new File(fileName));
        byte[] dataBytes = new byte[1024];
        int bytesRead = 0;

        while ((bytesRead = fis.read(dataBytes)) != -1) {
            md.update(dataBytes, 0, bytesRead);
        }

        byte[] mdBytes = md.digest();
        StringBuffer sb = new StringBuffer();

        for (int i = 0; i < mdBytes.length; i++) {
            sb.append(Integer.toString((mdBytes[i] & 0xff) + 0x100, 16).substring(1));
        }

        System.out.println("Checksum (MD5): " + sb.toString());
    }

    public static void main(String[] args) throws NoSuchAlgorithmException, IOException {
        setChecksumType("test.txt", "MD5");
    }

}

