import javax.el.ELContext;
import javax.el.ExpressionFactory;
import javax.el.ValueExpression;
import javax.el.FunctionMapper;
import javax.el.VariableMapper;

public class ELEvaluatorContext {

    public ELContext getContext() {
        // create an expression factory
        ExpressionFactory factory = ExpressionFactory.newInstance();

        // create a function mapper to map functions to method expressions
        FunctionMapper functionMapper = new FunctionMapper() {
            @Override
            public MethodExpression resolveFunction(String prefix, String localName) {
                // map the function name to a method expression
                if ("myFunction".equals(localName)) {
                    return factory.createMethodExpression(
                        new Object(), "myFunction", new Class<?>[]{String.class});
                }
                return null; // function not found
            }
        };

        // create a variable mapper to map variables to value expressions
        VariableMapper variableMapper = new VariableMapper() {
            @Override
            public ValueExpression resolveVariable(String variable) {
                // map the variable name to a value expression
                if ("myVariable".equals(variable)) {
                    return factory.createValueExpression("hello world", String.class);
                }
                return null; // variable not found
            }

            @Override
            public ValueExpression setVariable(String variable, ValueExpression expression) {
                // not supported
                return null;
            }
        };

        // create the EL context with the function and variable mappers
        ELContext context = new ELContext() {
            @Override
            public FunctionMapper getFunctionMapper() {
                return functionMapper;
            }

            @Override
            public VariableMapper getVariableMapper() {
                return variableMapper;
            }
        };

        return context;
    }

    // example method to demonstrate the use of the context
    public void evaluateExpression(String expression) {
        ExpressionFactory factory = ExpressionFactory.newInstance();
        ValueExpression valueExpression = factory.createValueExpression(getContext(), expression, Object.class);
        Object result = valueExpression.getValue(getContext());
        System.out.println(result);
    }

    // example usage
    public static void main(String[] args) {
        ELEvaluatorContext context = new ELEvaluatorContext();
        context.evaluateExpression("#{myFunction('test')}");
        context.evaluateExpression("#{myVariable}");
    }

    // example function
    public String myFunction(String arg) {
        return "myFunction result: " + arg;
    }
}

