public static Event[] processUprCommand(String command) {
    List<Event> events = new ArrayList<>();
    StringBuilder text = new StringBuilder();

    // Iterate over each character in the command
    for (int i = 0; i < command.length(); i++) {
        char c = command.charAt(i);

        // Check if the character is an escape character
        if (c == '\\') {
            // Parse the RTF command
            int commandEndIndex = command.indexOf(' ', i);
            if (commandEndIndex == -1) {
                commandEndIndex = command.length();
            }

            String rtfCommand = command.substring(i, commandEndIndex);
            i = commandEndIndex;

            // Check if the RTF command is related to UPR
            if (rtfCommand.startsWith("\\upr")) {
                // Extract the Unicode value from the RTF command
                int unicodeValue = Integer.parseInt(rtfCommand.substring(5));

                // Emit a Unicode event
                if (text.length() > 0) {
                    events.add(new TextEvent(text.toString()));
                    text.setLength(0);
                }
                events.add(new UnicodeEvent(unicodeValue));
            }
        } else {
            text.append(c);
        }
    }

    // Add any remaining text as a text event
    if (text.length() > 0) {
        events.add(new TextEvent(text.toString()));
    }

    return events.toArray(new Event[events.size()]);
}

