public static double computeEigenValue(double[][] A, double[] x) {
    // Compute the numerator xTAx
    double numerator = 0;
    for (int i = 0; i < A.length; i++) {
        for (int j = 0; j < A[0].length; j++) {
            numerator += x[i] * A[i][j] * x[j];
        }
    }

    // Compute the denominator xTx
    double denominator = 0;
    for (int i = 0; i < x.length; i++) {
        denominator += x[i] * x[i];
    }

    // Compute the eigenvalue using the Rayleigh quotient
    double eigenValue = numerator / denominator;

