public static double populationCovariance(double[] x, double[] y) {
    if (x.length != y.length || x.length < 2) {
        throw new IllegalArgumentException("Input arrays must have the same length and length must be at least 2.");
    }

    double sumX = 0.0;
    double sumY = 0.0;
    double sumXY = 0.0;
    double sumXSquared = 0.0;
    double sumYSquared = 0.0;
    int count = x.length;

    for (int i = 0; i < count; i++) {
        double xi = x[i];
        double yi = y[i];

        if (Double.isInfinite(xi) || Double.isInfinite(yi) || Double.isNaN(xi) || Double.isNaN(yi)) {
            return Double.NaN;
        }

        sumX += xi;
        sumY += yi;
        sumXY += xi * yi;
        sumXSquared += xi * xi;
        sumYSquared += yi * yi;
    }

    double covariance = (sumXY - (sumX * sumY) / count) / (count - 1);

    if (Double.isNaN(covariance)) {
        return 0.0;
    } else {
        return covariance;
    }
}

