import java.util.ArrayList;
import java.util.Collections;
import java.util.HashMap;

public class WordFrequency {
    public static void storeWordFrequencies(String text, ArrayList<String> words, ArrayList<Integer> frequencies) {
        HashMap<String, Integer> wordCounts = new HashMap<>();

        // Split text into words
        String[] textWords = text.split("\\s+");

        // Count the frequency of each word
        for (String word : textWords) {
            if (!wordCounts.containsKey(word)) {
                wordCounts.put(word, 1);
            } else {
                int count = wordCounts.get(word);
                wordCounts.put(word, count + 1);
            }
        }

        // Sort the words by frequency
        ArrayList<String> sortedWords = new ArrayList<>(wordCounts.keySet());
        Collections.sort(sortedWords, (a, b) -> wordCounts.get(a) - wordCounts.get(b));

        // Store the words and frequencies in parallel array lists
        for (String word : sortedWords) {
            words.add(word);
            frequencies.add(wordCounts.get(word));
        }
    }
}

