import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMResult;
import javax.xml.transform.dom.DOMSource;

import org.w3c.dom.Document;
import org.w3c.dom.NodeList;
import org.xmlunit.builder.DiffBuilder;
import org.xmlunit.diff.Diff;
import org.xmlunit.xpath.XPathEngine;
import org.xmlunit.xpath.XPathEngineImpl;

public class XmlUtils {
    public static Document executeCopyOfTransform(Document input, String xpathExpr) throws Exception {
        XPathEngine xpathEngine = new XPathEngineImpl();
        NodeList nodes = xpathEngine.selectNodes(xpathExpr, input);

        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        Document output = factory.newDocumentBuilder().newDocument();
        Transformer transformer = TransformerFactory.newInstance().newTransformer();
        for (int i = 0; i < nodes.getLength(); i++) {
            DOMResult result = new DOMResult();
            transformer.transform(new DOMSource(nodes.item(i)), result);
            output.adoptNode(result.getNode());
            output.appendChild(result.getNode());
        }
        return output;
    }

    public static Diff compareXml(String expected, String actual) {
        return DiffBuilder.compare(expected).withTest(actual).checkForSimilar().build();
    }
}

