public class XmlUtils {
    public static Document executeCopyOfTransform(Document input, String xpathExpr) throws Exception {
        XPathEngine xpathEngine = new XPathEngineImpl();
        NodeList nodes = xpathEngine.selectNodes(xpathExpr, input);

        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        Document output = factory.newDocumentBuilder().newDocument();
        Transformer transformer = TransformerFactory.newInstance().newTransformer();
        for (int i = 0; i < nodes.getLength(); i++) {
            DOMResult result = new DOMResult();
            transformer.transform(new DOMSource(nodes.item(i)), result);
            output.adoptNode(result.getNode());
