import java.io.File;

public static String checkAndModifyPath(String path) {
    // Check if path is null or empty
    if (path == null || path.isEmpty()) {
        return null;
    }

    // Create a File object from the path
    File file = new File(path);

    // Check if the path exists
    if (!file.exists()) {
        // Try to create the directory
        boolean success = file.mkdirs();

        // If the directory creation fails, return null
        if (!success) {
            return null;
        }
    }

    // Check if the path is a file
    if (file.isFile()) {
        // If it is a file, get the parent directory
        File parent = file.getParentFile();

        // If the parent directory does not exist, try to create it
        if (!parent.exists()) {
            boolean success = parent.mkdirs();

            // If the parent directory creation fails, return null
            if (!success) {
                return null;
            }
        }
    }

    // Return the modified path
    return file.getPath();
}

