import org.apache.wss4j.common.crypto.Crypto;
import org.apache.wss4j.common.crypto.CryptoFactory;
import org.apache.wss4j.dom.WSConstants;
import org.apache.wss4j.dom.message.WSSecEncryptedKey;
import org.apache.wss4j.dom.message.WSSecHeader;
import org.apache.wss4j.dom.util.WSSecurityUtil;
import org.w3c.dom.Document;
import org.w3c.dom.Element;

import javax.xml.parsers.DocumentBuilderFactory;
import java.io.FileInputStream;
import java.security.cert.X509Certificate;

public static String getKeyIdentifierType(String certPath, String certPassword) throws Exception {

    // Load the certificate
    FileInputStream fis = new FileInputStream(certPath);
    X509Certificate cert = (X509Certificate) CertificateFactory.getInstance("X.509").generateCertificate(fis);
    fis.close();

    // Create a WSS4J Crypto instance to handle the certificate
    Crypto crypto = CryptoFactory.getInstance();
    crypto.addCertificate(cert);

    // Create a new WSSecEncryptedKey object
    WSSecEncryptedKey encryptedKey = new WSSecEncryptedKey();

    // Set the key identifier type to be used
    encryptedKey.setKeyIdentifierType(WSConstants.X509_KEY_IDENTIFIER);

    // Set the certificate to be used for encryption
    encryptedKey.setUseThisCert(cert);

    // Create a new WSSecHeader object to store the encrypted key
    Document document = DocumentBuilderFactory.newInstance().newDocumentBuilder().newDocument();
    WSSecHeader header = new WSSecHeader();
    header.insertSecurityHeader(document);

    // Add the encrypted key to the security header
    Element encryptedKeyElement = encryptedKey.getEncryptedKeyElement(document);
    WSSecurityUtil.prependChildElement(header.getSecurityHeader(), encryptedKeyElement);

    // Get the key identifier type used to reference the certificate for encryption
    return encryptedKey.getKeyIdentifierType();
}

