public static double[][] multiplyByQ(double[][] B, double[][] householder) {
    int m = B.length;
    int n = B[0].length;

    // Multiply B by Householder reflectors from right to left
    for (int j = n - 1; j >= 0; j--) {
        for (int k = 0; k < m; k++) {
            double dotProduct = 0.0;
            for (int i = j; i < n; i++) {
                dotProduct += householder[i][j] * B[k][i];
            }
            for (int i = j; i < n; i++) {
                B[k][i] -= 2.0 * dotProduct * householder[i][j];
            }
        }
    }

    return B;
}

